ole" NameFormat="urn:oasis:names:tc:SAML:2.0:attrname-format:basic"><saml:AttributeValue xmlns:xs="http://www.w3.org/2001/XMLSchema" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xsi:type="xs:string">manage-account-links</saml:AttributeValue></saml:Attribute><saml:Attribute Name="Role" NameFormat="urn:oasis:names:tc:SAML:2.0:attrname-format:basic"><saml:AttributeValue xmlns:xs="http://www.w3.org/2001/XMLSchema" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xsi:type="xs:string">uma_authorization</saml:AttributeValue></saml:Attribute></saml:AttributeStatement></saml:Assertion></samlp:Response>';

    String ISSUER = 'http://127.0.0.1:8080/auth/realms/tercen';
    String AUDIENCE = 'http://127.0.0.1:5400/_service/sso/auth/saml';

    setUp(() {});

    test('First Test', () async {
      var saml =
          await Saml.fromCertificatePemFile('test/cert.pem', ISSUER, AUDIENCE);

      final response = SamlResponse(RESPONSE);

      expect(saml.validateResponse(response, validateTime: false), isTrue);

      expect(saml.validateResponse(response, validateTime: true), isFalse);

      saml =
          await Saml.fromCertificatePemFile('test/cert.pem', 'dummy', AUDIENCE);

      expect(saml.validateResponse(response, validateTime: false), isFalse);

      saml =
          await Saml.fromCertificatePemFile('test/cert.pem', ISSUER, 'dummy');

      expect(saml.validateResponse(response, validateTime: false), isFalse);
    });
